$(document).ready(function(){$(window).scroll(function(){return $("nav").toggleClass("fixed",$(window).scrollTop()>10)}),$("#nav-icon").click(function(){$("#menu").css({display:"flex"})}),$("[data-modal]").click(function(t){t.preventDefault(),$($(this).attr("href")).css({display:"flex"})}),$(".close, .nav-list li a").click(function(){$(this).parents(".modal").hide("200")}),$(".arr_down").click(function(){var t=$(".about").offset().top-0;$("body,html").animate({scrollTop:t},500)}),$(".cookies-btn").click(function(t){t.preventDefault(),$(this).parents(".cookies").hide("300")}),$(document).mouseup(function(t){var o=$(".modal .modal-content");o.is(t.target)||0!==o.has(t.target).length||o.parent().hide(200)}),$(".works-link").click(function(){$("#work .work-img img").attr("src",$(this).data("src"))}),$(".scroll").click(function(t){event.preventDefault();var o=$(this).attr("href"),n=$(o).offset().top;$("body,html").animate({scrollTop:n-40},1500)})});